import React, { useState } from 'react';
import './FoodMenu.css'
import { Container, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import fakeData from '../../fakeData/fakeData';

const FoodMenu = () => {
    const [foodItems, setFoodItems] = useState([{}]);

    const handleFoodItem = (category) =>{
        const selectedItems = fakeData.filter(item => item.category === category);
        setFoodItems({...foodItems}, selectedItems);
        console.log(foodItems);
    }
    return (
        <Container fluid>
            <Container>
                <Nav className="justify-content-center font-weight-bold p-5" activeKey="/home">
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("breakfast")}}>Breakfast</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("lunch")}}>Lunch</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("dinner")}}>Dinner</Link>
                    </Nav.Item>
                </Nav>
                <div className="row">
                    <div className="col-md-5">

                    </div>
                </div>
            </Container>
        </Container>
    );
};

export default FoodMenu;
