import React, { useState } from 'react';
import './FoodMenu.css'
import { Container, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import fakeData from '../../fakeData/fakeData';

const FoodMenu = () => {
    const [foodItems, setFoodItems] = useState([]);
    const [isClicked, setIsClicked] = useState(false);

    // onload = ("lunch") => {
        
    // }

    const handleFoodItem = (category) =>{
        setFoodItems()
        const selectedItems = fakeData.filter(item => item.category === category);
        setFoodItems(selectedItems);
        
    }
    // console.log(foodItems);
    return (
        <Container fluid>
            <Container>
                <Nav className="justify-content-center font-weight-bold p-5" activeKey="/home">
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("breakfast")}}>Breakfast</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("lunch")}}>Lunch</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink" to="" onClick={() => {handleFoodItem("dinner")}}>Dinner</Link>
                    </Nav.Item>
                </Nav>
                isClicked
                ?<div className="row">
                    {
                        foodItems.map(item => 
                            <div className="col-md-4">
                                <img src={item.image} className="" alt=""/>
                                <h5>{item.title}</h5>
                                <p className="text-muted">{item.info}</p>
                                <h2>${item.price}</h2>
                            </div>    
                        )
                    }
                </div>
                :<div className="row">
                    {
                        foodItems.map(item => 
                            <div className="col-md-4">
                                <img src={item.image} className="" alt=""/>
                                <h5>{item.title}</h5>
                                <p className="text-muted">{item.info}</p>
                                <h2>${item.price}</h2>
                            </div>    
                        )
                    }
                </div>
            </Container>
        </Container>
    );
};

export default FoodMenu;
