import React, { useState } from 'react';
import './FoodMenu.css'
import { Container, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import fakeData from '../../fakeData/fakeData';

const FoodMenu = () => {
    const [foodItems, setFoodItems] = useState([fakeData.filter(item => item.category === "lunch")]);
    const [isClicked, setIsClicked] = useState(false);

    if(isClicked){
        const defaultItems = fakeData.filter(item => item.category === "lunch");
    setFoodItems(defaultItems);
    }    

    const handleFoodItem = (category) =>{
        setIsClicked(true);
        const selectedItems = fakeData.filter(item => item.category === category);
        setFoodItems(selectedItems);
        
    }
    // console.log(foodItems);
    return (
        <Container fluid>
            <Container>
                <Nav className="justify-content-center font-weight-bold p-5" activeKey="/home">
                    <Nav.Item>
                        <Link className={isClicked ? "navLink" : "mr-3"} to="" onClick={() => {handleFoodItem("breakfast")}}>Breakfast</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className={isClicked ? "navLink" : "mr-3"} to="" onClick={() => {handleFoodItem("lunch")}} >Lunch</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className={isClicked ? "navLink" : "mr-3"} to="" onClick={() => {handleFoodItem("dinner")}}>Dinner</Link>
                    </Nav.Item>
                </Nav>
                <div className="row p-5">
                    {
                        foodItems.map(item => 
                            <div className="col-md-4">
                                <img src={item.image} className="w-50" alt=""/>
                                <h5>{item.title}</h5>
                                <p className="text-muted">{item.info}</p>
                                <h2>${item.price}</h2>
                            </div>    
                        )
                    }
                </div>
                <button className="rounded p-2">Checkout Your Food</button>
            </Container>
        </Container>
    );
};

export default FoodMenu;
