import React from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';

const Login = () => {
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data) => {
        console.log(data);
    }

    return (
        <div>
            <div className="p-5">
                <Link to="/home"><img src="https://iili.io/27JGwB.png" style={{ width: 200 }} className="d-inline-block align-top" alt="" /></Link>
            </div>
            <div className="w-50 mx-auto p-5">
                <form onSubmit={handleSubmit(onSubmit)}>
                    
                    <input name="name" className="form-control bg-light" defaultValue="" ref={register({ required: true })} placeholder="Your Name"/>
                    {errors.name && <span className="error">Name is required</span>}
                    <br/>

                    <input type="submit" />
                </form>
            </div>
        </div>
    );
};

export default Login;
