import React, { useContext } from 'react';
import { Container, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { UserContext } from '../../App';

const ItemDetails = () => {
    const [itemDetails, setItemDetails] = useContext(UserContext);
    return (
        <Container fluid>
            <Container>
                <Nav className="justify-content-center font-weight-bold p-5" activeKey="/home">
                    <Nav.Item>
                        <Link className="navLink mr-3" to="/">Breakfast</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink mr-3" to="/" >Lunch</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link className="navLink mr-3" to="/">Dinner</Link>
                    </Nav.Item>
                </Nav>
                {
                    itemDetails.map(item =>
                        <div className="row">
                            <div className="col-md-6">
                                
                            </div>
                            <div className="col-md-6"></div>
                        </div>    
                    )
                }
            </Container>
        </Container>
    );
};

export default ItemDetails;
